import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const ResultPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { results } = location.state || { results: null };

  if (!results) {
    return (
      <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center p-4">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-red-500">Analysis Failed</h2>
          <p className="text-gray-600 mt-2">No results were found. Please try again.</p>
          <button onClick={() => navigate('/details')} className="mt-6 text-white bg-blue-600 hover:bg-blue-700 font-semibold rounded-lg text-sm px-5 py-3">
            Start New Analysis
          </button>
        </div>
      </div>
    );
  }

  const scoreColor = results.finalScore > 50 ? 'text-red-500' : 'text-green-500';

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center p-4">
      <div className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow-lg">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-gray-800">Analysis Results</h2>
          <p className="text-gray-500 mt-2">Step 4 of 4: Review your report.</p>
        </div>
        <div className={`text-center my-8 p-6 bg-gray-50 rounded-xl`}>
          <p className="text-lg font-semibold text-gray-600">Overall Alzheimer's Risk Score</p>
          <p className={`text-7xl font-bold ${scoreColor}`}>{results.finalScore}%</p>
        </div>
        <div className="space-y-4">
          <h3 className="text-lg font-semibold text-gray-700">Score Breakdown:</h3>
          <div className="flex justify-between items-center p-4 bg-blue-50 rounded-lg">
            <span className="font-medium">Cognitive Quiz Impairment</span>
            <span className="font-bold text-blue-600">{results.quiz}%</span>
          </div>
          <div className="flex justify-between items-center p-4 bg-green-50 rounded-lg">
            <span className="font-medium">MRI Scan Analysis</span>
            <span className="font-bold text-green-600">{results.mri}%</span>
          </div>
          <div className="flex justify-between items-center p-4 bg-yellow-50 rounded-lg">
            <span className="font-medium">Voice Pattern Analysis</span>
            <span className="font-bold text-yellow-600">{results.voice}%</span>
          </div>
        </div>
        <div className="mt-8 text-center">
          <p className="text-xs text-gray-500 italic">
            <strong>Disclaimer:</strong> This is a demo application. The results are illustrative and should not be considered a medical diagnosis.
          </p>
          <button onClick={() => navigate('/details')} className="mt-6 w-full text-white bg-blue-600 hover:bg-blue-700 font-semibold rounded-lg text-sm px-5 py-3">
            Start a New Analysis
          </button>
        </div>
      </div>
    </div>
  );
};

export default ResultPage;
