import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', formData);
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/details'); // Navigate to details page after login
    } catch (err) {
      alert('Error: ' + (err.response?.data?.msg || 'Please check your credentials.'));
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={onSubmit}>
        <input type="email" placeholder="Email" name="email" onChange={onChange} required />
        <input type="password" placeholder="Password" name="password" onChange={onChange} required />
        <input type="submit" value="Login" />
      </form>
    </div>
  );
};

export default LoginPage;
