import React, { useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UploadPage = () => {
  const [mriFile, setMriFile] = useState(null);
  const [status, setStatus] = useState('');
  const location = useLocation();
  const navigate = useNavigate();
  const { personalDetails, quizScore } = location.state || {};

  // States for voice recording
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const mediaRecorder = useRef(null);
  const audioChunks = useRef([]);

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorder.current = new MediaRecorder(stream);
      
      mediaRecorder.current.ondataavailable = (event) => {
        audioChunks.current.push(event.data);
      };

      mediaRecorder.current.onstop = () => {
        const blob = new Blob(audioChunks.current, { type: 'audio/wav' });
        setAudioBlob(blob);
        audioChunks.current = [];
      };
      
      mediaRecorder.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error("Error accessing microphone:", error);
      alert("Microphone access is required for voice recording.");
    }
  };

  const handleStopRecording = () => {
    mediaRecorder.current.stop();
    setIsRecording(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!mriFile) {
      alert('Please upload an MRI scan.');
      return;
    }
    setStatus('Analyzing...');
    
    const formData = new FormData();
    formData.append('mriScan', mriFile);
    formData.append('quizScore', quizScore);
    if (audioBlob) {
      formData.append('voiceRecording', audioBlob, 'recording.wav');
    }
    
    try {
      const res = await axios.post('http://localhost:5000/api/analysis/submit', formData);
      navigate('/results', { state: { results: res.data.results } });
    } catch (err) {
      setStatus('Analysis failed. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4">
      <div className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow-xl">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-2">Upload Medical Data</h2>
        <p className="text-center text-gray-600 mb-8">Provide your MRI scan and a voice sample for comprehensive analysis.</p>
        
        <form onSubmit={handleSubmit} className="space-y-8">
          {/* MRI Upload */}
          <div className="bg-gray-50 p-6 rounded-lg shadow-sm">
            <label className="block text-md font-semibold text-gray-700 mb-2">1. Upload MRI Scan</label>
            <p className="text-sm text-gray-500 mb-4">Upload a clear MRI image of the brain for our AI analysis.</p>
            <input 
              type="file" 
              onChange={(e) => setMriFile(e.target.files[0])} 
              required 
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          </div>

          {/* Voice Recording */}
          <div className="bg-gray-50 p-6 rounded-lg shadow-sm">
            <label className="block text-md font-semibold text-gray-700 mb-2">2. Record Voice Sample</label>
            <p className="text-sm text-gray-500 mb-4">Click "Start Recording" and read a short sentence clearly. <br/> (e.g., "The quick brown fox jumps over the lazy dog.")</p>
            {!isRecording ? (
              <button type="button" onClick={handleStartRecording} className="w-full font-semibold text-white bg-green-600 hover:bg-green-700 py-3 px-4 rounded-lg transition-all">
                Start Recording
              </button>
            ) : (
              <button type="button" onClick={handleStopRecording} className="w-full font-semibold text-white bg-red-600 hover:bg-red-700 py-3 px-4 rounded-lg transition-all">
                Stop Recording
              </button>
            )}
            {audioBlob && (
              <div className="mt-4 text-center">
                <p className="text-sm font-medium text-green-700 mb-2">Recording complete! Play to review:</p>
                <audio src={URL.createObjectURL(audioBlob)} controls className="w-full max-w-sm mx-auto" />
              </div>
            )}
          </div>

          {/* Submit Button */}
          <div>
            <button type="submit" disabled={!!status} className="w-full text-white bg-gradient-to-r from-blue-600 to-indigo-700 hover:from-blue-700 hover:to-indigo-800 disabled:from-gray-400 disabled:to-gray-500 font-bold rounded-lg text-lg px-5 py-3 transition-all">
              {status ? status : 'Get Analysis Results'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UploadPage;
