import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SignupPage = () => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const navigate = useNavigate();

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/register', formData);
      alert('Registration successful! Please log in.');
      navigate('/login');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.msg || 'Please try again.'));
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={onSubmit}>
        <input type="text" placeholder="Name" name="name" onChange={onChange} required />
        <input type="email" placeholder="Email" name="email" onChange={onChange} required />
        <input type="password" placeholder="Password (min 6 chars)" name="password" onChange={onChange} minLength="6" required />
        <input type="submit" value="Sign Up" />
      </form>
    </div>
  );
};

export default SignupPage;
