import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => { // This is your combined Welcome and Login Page
  const [formData, setFormData] = useState({ email: '', password: '' });
  const navigate = useNavigate();

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', formData);
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/details');
    } catch (err) {
      alert('Error: ' + (err.response?.data?.msg || 'Please check your credentials. New accounts are created automatically upon first login.'));
    }
  };

  return (
    <div className="min-h-full flex items-center justify-center p-4 sm:p-6 lg:p-8 bg-gray-100">
      <div className="w-full max-w-5xl bg-white rounded-2xl shadow-2xl overflow-hidden grid lg:grid-cols-2">
        
        {/* Left Column: Image with Black Background */}
        <div className="hidden lg:flex flex-col items-center justify-center bg-black p-12 text-white text-center">
          <img 
            src="/images/brain-mri.png" 
            alt="Brain MRI Scan" 
            className="w-full max-w-sm rounded-lg shadow-xl mb-8"
          />
          <h1 className="text-3xl font-bold tracking-tight">NeuroAssess Insight</h1>
          <p className="mt-4 text-gray-300">Advanced analysis for early detection and peace of mind.</p>
        </div>

        {/* Right Column: Login Form */}
        <div className="p-8 sm:p-12 flex flex-col justify-center">
          <h2 className="text-4xl md:text-5xl font-extrabold text-gray-800 mb-4 text-center">Welcome Back!</h2>
          <p className="text-center text-gray-600 mb-10 text-lg">
            Sign in to access your comprehensive Alzheimer's assessment.
          </p>

          <div className="max-w-md mx-auto w-full">
            <form onSubmit={onSubmit} className="space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email Address</label>
                <input type="email" name="email" onChange={onChange} required
                  className="mt-1 block w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                  placeholder="Enter your email"
                />
              </div>
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
                <input type="password" name="password" onChange={onChange} required
                  className="mt-1 block w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                  placeholder="Enter your password"
                />
              </div>
              <div>
                <button type="submit" className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-lg text-lg font-bold text-white bg-gradient-to-r from-red-500 to-green-500 hover:from-red-600 hover:to-green-600 transition-all duration-300 ease-in-out">
                  Sign In & Continue
                </button>
              </div>
            </form>
            <p className="text-center text-gray-500 mt-6 text-sm">
              New user? Your account will be created automatically upon first login.
              {/* If you still want a separate signup form: */}
              {/* <Link to="/signup" className="font-semibold text-indigo-600 hover:text-indigo-800 ml-1">Sign Up Here</Link> */}
            </p>
          </div>
        </div>

      </div>
    </div>
  );
};

export default LoginPage;
