import React, { useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UploadPage = () => {
  const [mriFile, setMriFile] = useState(null);
  const [status, setStatus] = useState('');
  const location = useLocation();
  const navigate = useNavigate();
  const { personalDetails, quizScore } = location.state || {};
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const mediaRecorder = useRef(null);
  const audioChunks = useRef([]);

  const handleStartRecording = async () => { /* ... same as before ... */ };
  const handleStopRecording = () => { /* ... same as before ... */ };
  const handleSubmit = async (e) => { /* ... same as before ... */ };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center p-4">
      <div className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow-lg">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Upload Medical Data</h2>
          <p className="text-gray-500">Step 3 of 4: Provide your MRI and a voice sample.</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-8">
          <div>
            <label className="block text-md font-semibold text-gray-700">1. Upload MRI Scan</label>
            <p className="text-sm text-gray-500 mb-2">Please upload a clear MRI image of the brain.</p>
            <input type="file" onChange={(e) => setMriFile(e.target.files[0])} required className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"/>
          </div>
          <div>
            <label className="block text-md font-semibold text-gray-700">2. Record Voice Sample</label>
            <p className="text-sm text-gray-500 mb-2">Click start and read a short sentence clearly.</p>
            {!isRecording ? (
              <button type="button" onClick={handleStartRecording} className="w-full font-semibold text-white bg-green-500 hover:bg-green-600 py-3 px-4 rounded-lg transition-all">
                Start Recording
              </button>
            ) : (
              <button type="button" onClick={handleStopRecording} className="w-full font-semibold text-white bg-red-500 hover:bg-red-600 py-3 px-4 rounded-lg transition-all">
                Stop Recording
              </button>
            )}
            {audioBlob && (
              <div className="mt-4">
                <p className="text-sm text-center font-medium text-green-700">Recording complete!</p>
                <audio src={URL.createObjectURL(audioBlob)} controls className="w-full mt-2" />
              </div>
            )}
          </div>
          <div>
            <button type="submit" disabled={!!status} className="w-full text-white bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 font-semibold rounded-lg text-sm px-5 py-3 transition-all">
              {status ? status : 'Get Analysis Results'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UploadPage;
