import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar'; // Keep Navbar for now, but it will be very minimal
// Removed: import ProgressBar from './components/ProgressBar';

// Import all the pages you are actually using
import SignupPage from './pages/SignupPage';
import LoginPage from './pages/LoginPage'; // This is now your main welcome/login page
import DetailsPage from './pages/DetailsPage';
import QuizPage from './pages/QuizPage';
import UploadPage from './pages/UploadPage';
import ResultPage from './pages/ResultPage';

function App() {
  // For other pages (Details, Quiz, Upload, Results), we might want a progress bar or shared header.
  // For the login/signup pages, we are making them full-screen themed.
  const showProgressBar = ['/details', '/quiz', '/upload', '/results'].includes(window.location.pathname);
  // We'll keep the Navbar here for now, but it's very minimal. The actual 'NeuroAssess' title is in LoginPage.

  return (
    <Router>
      <div className="min-h-screen flex flex-col">
        {/* We are removing the Navbar from the initial login/signup pages for a full-page design */}
        {/* If you decide to add a header/navbar to the assessment flow, you can make this conditional */}
        {/* <Navbar />  */} 
        
        {/* Temporarily removing ProgressBar from App.js based on your request */}
        {/* {showProgressBar && <ProgressBar />} */}

        <main className="flex-grow">
          <Routes>
            {/* The root path is now the full-page login experience */}
            <Route path="/" element={<LoginPage />} /> 
            <Route path="/signup" element={<SignupPage />} />

            {/* Analysis Flow Routes - these might need a different shared layout later */}
            <Route path="/details" element={<DetailsPage />} />
            <Route path="/quiz" element={<QuizPage />} />
            <Route path="/upload" element={<UploadPage />} />
            <Route path="/results" element={<ResultPage />} />

            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
