import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const DetailsPage = () => {
  const [details, setDetails] = useState({ age: '', gender: '' });
  const navigate = useNavigate();

  const onChange = (e) => setDetails({ ...details, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    console.log('User Details:', details);
    navigate('/quiz', { state: { personalDetails: details } });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center p-4">
      <div className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-lg">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Personal Details</h2>
          <p className="text-gray-500">Step 1 of 4: Please provide some basic information.</p>
        </div>
        <form onSubmit={onSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-semibold text-gray-600">Age</label>
            <input type="number" placeholder="Enter your age" name="age" onChange={onChange} required className="mt-1 block w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"/>
          </div>
          <div>
            <label className="block text-sm font-semibold text-gray-600">Gender</label>
            <select name="gender" onChange={onChange} required className="mt-1 block w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500">
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div>
            <button type="submit" className="w-full text-white bg-blue-600 hover:bg-blue-700 font-semibold rounded-lg text-sm px-5 py-3 transition-all">
              Proceed to Quiz
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DetailsPage;
