import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const questions = [
  { text: "What is today's date?", points: 5 },
  { text: 'Spell the word "WORLD" backwards.', points: 5 },
  { text: 'Name a common household pet.', points: 5 },
  { text: 'Repeat these 3 words: Apple, Table, Penny.', points: 5 },
  { text: 'Count backwards from 50 by 3s.', points: 5 },
  { text: 'What city are we in right now?', points: 5 },
];

const QuizPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { personalDetails } = location.state || {};
  
  const handleSubmit = (e) => {
    e.preventDefault();
    const mockScore = 22; // Example score out of 30
    console.log('Quiz Score:', mockScore);
    navigate('/upload', { state: { personalDetails, quizScore: mockScore } });
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center p-4">
      <div className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow-lg">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800">Cognitive Quiz</h2>
          <p className="text-gray-500">Step 2 of 4: Please answer the following questions.</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-6">
          {questions.map((q, index) => (
            <div key={index}>
              <label className="block text-md font-semibold text-gray-700">{index + 1}. {q.text}</label>
              <input type="text" required className="mt-2 block w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500"/>
            </div>
          ))}
          <div>
            <button type="submit" className="w-full text-white bg-blue-600 hover:bg-blue-700 font-semibold rounded-lg text-sm px-5 py-3 transition-all">
              Submit Quiz & Proceed
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuizPage;
