import React from 'react';
import { useLocation } from 'react-router-dom';

const ProgressBar = () => {
  const location = useLocation();
  const path = location.pathname;

  const steps = [
    { name: 'Login/Signup', path: ['/login', '/signup'] },
    { name: 'Details', path: '/details' },
    { name: 'Quiz', path: '/quiz' },
    { name: 'Upload', path: '/upload' },
    { name: 'Results', path: '/results' },
    // You can add more steps if needed
  ];

  const getCurrentStepIndex = () => {
    const currentStep = steps.findIndex(step => 
      Array.isArray(step.path) ? step.path.includes(path) : step.path === path
    );
    return currentStep !== -1 ? currentStep : 0; // Default to first step if path not found
  };

  const activeStepIndex = getCurrentStepIndex();

  return (
    <div className="flex justify-center my-8">
      <div className="flex space-x-2">
        {steps.map((step, index) => (
          <div
            key={index}
            className={`w-10 h-10 rounded-full flex items-center justify-center font-bold text-white transition-all duration-300 ease-in-out
              ${index <= activeStepIndex ? 'bg-gradient-to-br from-indigo-500 to-blue-600 shadow-md' : 'bg-gray-300 text-gray-700'}`}
            title={step.name}
          >
            {index + 1}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProgressBar;
