import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProgressBar from './components/ProgressBar';

// Import all page components
import SignupPage from './pages/SignupPage';
import SignInPage from './pages/SignInPage'; // NEW: Separate login form
import LoginPage from './pages/LoginPage'; // Now the 'Welcome' page
import DetailsPage from './pages/DetailsPage';
import QuizPage from './pages/QuizPage';
import UploadPage from './pages/UploadPage';
import ResultPage from './pages/ResultPage';

function App() {
  return (
    <Router>
      <div className="min-h-screen flex flex-col"> {/* Use flex-col for proper layout */}
        <Navbar />
        <ProgressBar />
        <main className="flex-grow"> {/* `flex-grow` makes it take available space, no default padding here */}
          <Routes>
            {/* Authentication & Welcome Routes */}
            <Route path="/" element={<LoginPage />} /> {/* Welcome page as default */}
            <Route path="/signup" element={<SignupPage />} />
            <Route path="/signin" element={<SignInPage />} /> {/* Dedicated sign-in form */}

            {/* Analysis Flow Routes */}
            <Route path="/details" element={<DetailsPage />} />
            <Route path="/quiz" element={<QuizPage />} />
            <Route path="/upload" element={<UploadPage />} />
            <Route path="/results" element={<ResultPage />} />

            {/* If a route is not found, redirect to welcome page */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
